// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/access/Ownable2Step.sol";

/**
 * @title ChannelRegistry
 * @notice Registry for managing state channels used in deal settlements
 * @dev Integrates with Yellow/Nitrolite protocol for off-chain state management
 */
contract ChannelRegistry is Ownable2Step {
    /// @notice Channel state
    enum ChannelState {
        Nonexistent,
        Open,
        Disputed,
        Finalized,
        Closed
    }

    /// @notice Channel data
    struct Channel {
        bytes32 channelId;          // Unique channel identifier
        address dealer;             // Deal vault address
        address counterparty;       // Remote chain or external party
        uint256 dealId;             // Associated deal ID
        uint256 depositAmount;      // Amount locked in channel
        uint256 openedAt;           // Timestamp when channel opened
        uint256 finalizedAt;        // Timestamp when finalized
        ChannelState state;         // Current channel state
        bytes32 stateRoot;          // Latest state root hash
        uint256 nonce;              // State update nonce
    }

    /// @notice Mapping from channel ID to channel data
    mapping(bytes32 => Channel) public channels;

    /// @notice Mapping from deal ID to list of channel IDs
    mapping(uint256 => bytes32[]) public dealChannels;

    /// @notice Authorized operators that can manage channels
    mapping(address => bool) public operators;

    /// @notice Challenge period in seconds
    uint256 public challengePeriod = 24 hours;

    /// @notice Events
    event ChannelOpened(
        bytes32 indexed channelId,
        address indexed dealer,
        uint256 indexed dealId,
        uint256 depositAmount
    );
    event ChannelStateUpdated(
        bytes32 indexed channelId,
        bytes32 stateRoot,
        uint256 nonce
    );
    event ChannelDisputed(
        bytes32 indexed channelId,
        address indexed challenger
    );
    event ChannelFinalized(
        bytes32 indexed channelId,
        bytes32 finalStateRoot
    );
    event ChannelClosed(bytes32 indexed channelId);
    event OperatorSet(address indexed operator, bool authorized);
    event ChallengePeriodUpdated(uint256 newPeriod);

    /// @notice Custom errors
    error UnauthorizedOperator();
    error ChannelAlreadyExists();
    error ChannelNotFound();
    error InvalidChannelState();
    error ChallengePeriodNotElapsed();
    error InvalidStateTransition();
    error InvalidNonce();

    /// @notice Modifier to check if caller is authorized operator
    modifier onlyOperator() {
        if (!operators[msg.sender] && msg.sender != owner()) {
            revert UnauthorizedOperator();
        }
        _;
    }

    /**
     * @notice Constructor
     * @param initialOwner Address that will own the contract
     */
    constructor(address initialOwner) Ownable(initialOwner) {
        operators[initialOwner] = true;
    }

    /**
     * @notice Open a new state channel
     * @param channelId Unique channel identifier
     * @param dealer Deal vault address
     * @param counterparty Remote party address
     * @param dealId Associated deal ID
     * @param depositAmount Amount to lock in channel
     */
    function openChannel(
        bytes32 channelId,
        address dealer,
        address counterparty,
        uint256 dealId,
        uint256 depositAmount
    ) external onlyOperator {
        if (channels[channelId].state != ChannelState.Nonexistent) {
            revert ChannelAlreadyExists();
        }

        channels[channelId] = Channel({
            channelId: channelId,
            dealer: dealer,
            counterparty: counterparty,
            dealId: dealId,
            depositAmount: depositAmount,
            openedAt: block.timestamp,
            finalizedAt: 0,
            state: ChannelState.Open,
            stateRoot: bytes32(0),
            nonce: 0
        });

        dealChannels[dealId].push(channelId);

        emit ChannelOpened(channelId, dealer, dealId, depositAmount);
    }

    /**
     * @notice Update channel state
     * @param channelId Channel identifier
     * @param stateRoot New state root hash
     * @param nonce State update nonce
     */
    function updateChannelState(
        bytes32 channelId,
        bytes32 stateRoot,
        uint256 nonce
    ) external onlyOperator {
        Channel storage channel = channels[channelId];

        if (channel.state == ChannelState.Nonexistent) {
            revert ChannelNotFound();
        }
        if (channel.state != ChannelState.Open) {
            revert InvalidChannelState();
        }
        if (nonce <= channel.nonce) {
            revert InvalidNonce();
        }

        channel.stateRoot = stateRoot;
        channel.nonce = nonce;

        emit ChannelStateUpdated(channelId, stateRoot, nonce);
    }

    /**
     * @notice Initiate dispute for a channel
     * @param channelId Channel identifier
     */
    function disputeChannel(bytes32 channelId) external {
        Channel storage channel = channels[channelId];

        if (channel.state == ChannelState.Nonexistent) {
            revert ChannelNotFound();
        }
        if (channel.state != ChannelState.Open) {
            revert InvalidChannelState();
        }

        channel.state = ChannelState.Disputed;

        emit ChannelDisputed(channelId, msg.sender);
    }

    /**
     * @notice Finalize channel state (start challenge period)
     * @param channelId Channel identifier
     * @param finalStateRoot Final state root hash
     */
    function finalizeChannel(
        bytes32 channelId,
        bytes32 finalStateRoot
    ) external onlyOperator {
        Channel storage channel = channels[channelId];

        if (channel.state == ChannelState.Nonexistent) {
            revert ChannelNotFound();
        }
        if (channel.state != ChannelState.Open && channel.state != ChannelState.Disputed) {
            revert InvalidChannelState();
        }

        channel.state = ChannelState.Finalized;
        channel.stateRoot = finalStateRoot;
        channel.finalizedAt = block.timestamp;

        emit ChannelFinalized(channelId, finalStateRoot);
    }

    /**
     * @notice Close channel after challenge period
     * @param channelId Channel identifier
     */
    function closeChannel(bytes32 channelId) external onlyOperator {
        Channel storage channel = channels[channelId];

        if (channel.state == ChannelState.Nonexistent) {
            revert ChannelNotFound();
        }
        if (channel.state != ChannelState.Finalized) {
            revert InvalidChannelState();
        }
        if (block.timestamp < channel.finalizedAt + challengePeriod) {
            revert ChallengePeriodNotElapsed();
        }

        channel.state = ChannelState.Closed;

        emit ChannelClosed(channelId);
    }

    /**
     * @notice Set operator authorization
     * @param operator Address of the operator
     * @param authorized Whether to authorize or revoke
     */
    function setOperator(address operator, bool authorized) external onlyOwner {
        operators[operator] = authorized;
        emit OperatorSet(operator, authorized);
    }

    /**
     * @notice Update challenge period
     * @param newPeriod New challenge period in seconds
     */
    function setChallengePeriod(uint256 newPeriod) external onlyOwner {
        challengePeriod = newPeriod;
        emit ChallengePeriodUpdated(newPeriod);
    }

    /**
     * @notice Get channel data
     * @param channelId Channel identifier
     * @return Channel data
     */
    function getChannel(bytes32 channelId) external view returns (Channel memory) {
        return channels[channelId];
    }

    /**
     * @notice Get all channels for a deal
     * @param dealId Deal identifier
     * @return Array of channel IDs
     */
    function getDealChannels(uint256 dealId) external view returns (bytes32[] memory) {
        return dealChannels[dealId];
    }

    /**
     * @notice Check if channel can be closed
     * @param channelId Channel identifier
     * @return True if channel can be closed
     */
    function canCloseChannel(bytes32 channelId) external view returns (bool) {
        Channel storage channel = channels[channelId];
        return channel.state == ChannelState.Finalized &&
               block.timestamp >= channel.finalizedAt + challengePeriod;
    }
}

project/contracts/core/DealFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/access/Ownable2Step.sol";
import "./DealPosition.sol";
import "./ChannelRegistry.sol";
import "./DealVault.sol";
import "../integrations/flare/FlarePriceReader.sol";

/**
 * @title DealFactory
 * @notice Factory contract for deploying and managing Deal infrastructure
 * @dev Simplifies deployment and provides a registry of all vaults
 */
contract DealFactory is Ownable2Step {
    /// @notice Position NFT contract
    DealPosition public immutable positionNFT;

    /// @notice Channel registry
    ChannelRegistry public immutable channelRegistry;

    /// @notice Price reader
    FlarePriceReader public immutable priceReader;

    /// @notice Array of all deployed vaults
    address[] public vaults;

    /// @notice Mapping to check if address is a vault
    mapping(address => bool) public isVault;

    /// @notice Default fee recipient
    address public defaultFeeRecipient;

    /// @notice Events
    event VaultDeployed(address indexed vault, address indexed creator);
    event DefaultFeeRecipientUpdated(address indexed newRecipient);

    /// @notice Custom errors
    error InvalidAddress();

    /**
     * @notice Constructor
     * @param initialOwner Address that will own the contract
     * @param ftsoRegistry Address of the FTSO registry for price feeds
     * @param _defaultFeeRecipient Default fee recipient for new vaults
     */
    constructor(
        address initialOwner,
        address ftsoRegistry,
        address _defaultFeeRecipient
    ) Ownable(initialOwner) {
        if (ftsoRegistry == address(0) || _defaultFeeRecipient == address(0)) {
            revert InvalidAddress();
        }

        // Deploy core infrastructure
        positionNFT = new DealPosition(address(this));
        channelRegistry = new ChannelRegistry(address(this));
        priceReader = new FlarePriceReader(address(this), ftsoRegistry);

        defaultFeeRecipient = _defaultFeeRecipient;

        // Transfer ownership to initialOwner
        positionNFT.transferOwnership(initialOwner);
        channelRegistry.transferOwnership(initialOwner);
        priceReader.transferOwnership(initialOwner);
    }

    /**
     * @notice Deploy a new DealVault
     * @param feeRecipient Fee recipient for this vault (use address(0) for default)
     * @return vault Address of the deployed vault
     */
    function deployVault(address feeRecipient) 
        external 
        onlyOwner 
        returns (address vault) 
    {
        address recipient = feeRecipient == address(0) ? defaultFeeRecipient : feeRecipient;

        // Deploy new vault
        DealVault newVault = new DealVault(
            owner(),
            address(positionNFT),
            address(channelRegistry),
            address(priceReader),
            recipient
        );

        vault = address(newVault);

        // Register vault
        vaults.push(vault);
        isVault[vault] = true;

        // Authorize vault to mint position NFTs
        positionNFT.setVaultAuthorization(vault, true);

        // Authorize vault as channel operator
        channelRegistry.setOperator(vault, true);

        emit VaultDeployed(vault, msg.sender);
    }

    /**
     * @notice Update default fee recipient
     * @param newRecipient New default fee recipient
     */
    function setDefaultFeeRecipient(address newRecipient) external onlyOwner {
        if (newRecipient == address(0)) revert InvalidAddress();
        defaultFeeRecipient = newRecipient;
        emit DefaultFeeRecipientUpdated(newRecipient);
    }

    /**
     * @notice Get total number of deployed vaults
     * @return count Number of vaults
     */
    function getVaultCount() external view returns (uint256) {
        return vaults.length;
    }

    /**
     * @notice Get all deployed vaults
     * @return Array of vault addresses
     */
    function getAllVaults() external view returns (address[] memory) {
        return vaults;
    }

    /**
     * @notice Get vault at specific index
     * @param index Vault index
     * @return Vault address
     */
    function getVault(uint256 index) external view returns (address) {
        require(index < vaults.length, "Index out of bounds");
        return vaults[index];
    }

    /**
     * @notice Get core contract addresses
     * @return position Address of position NFT
     * @return registry Address of channel registry
     * @return prices Address of price reader
     */
    function getCoreContracts() 
        external 
        view 
        returns (
            address position,
            address registry,
            address prices
        ) 
    {
        return (
            address(positionNFT),
            address(channelRegistry),
            address(priceReader)
        );
    }
}

project/contracts/core/DealPosition.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";

/**
 * @title DealPosition
 * @notice ERC-721 NFT representing LP positions in deals
 * @dev Each position NFT represents a deposit in a specific deal vault
 */
contract DealPosition is ERC721, ERC721Enumerable, Ownable2Step {
    /// @notice Position metadata
    struct Position {
        uint256 dealId;           // ID of the deal this position belongs to
        uint256 depositAmount;    // Amount deposited (in deposit token decimals)
        uint256 depositTime;      // Timestamp of deposit
        bool claimed;             // Whether this position has been claimed
    }

    /// @notice Mapping from token ID to position data
    mapping(uint256 => Position) public positions;

    /// @notice Authorized deal vaults that can mint positions
    mapping(address => bool) public authorizedVaults;

    /// @notice Counter for position token IDs
    uint256 private _nextTokenId;

    /// @notice Events
    event VaultAuthorized(address indexed vault, bool authorized);
    event PositionMinted(
        uint256 indexed tokenId,
        address indexed to,
        uint256 dealId,
        uint256 depositAmount
    );
    event PositionBurned(uint256 indexed tokenId, address indexed from);

    /// @notice Custom errors
    error UnauthorizedVault();
    error InvalidRecipient();
    error PositionAlreadyClaimed();

    /**
     * @notice Constructor
     * @param initialOwner Address that will own the contract
     */
    constructor(address initialOwner) 
        ERC721("Liquium Position", "LIQ-POS") 
        Ownable(initialOwner)
    {
        _nextTokenId = 1; // Start token IDs at 1
    }

    /**
     * @notice Mint a new position NFT
     * @param to Address to mint the NFT to
     * @param dealId ID of the deal
     * @param depositAmount Amount deposited
     * @return tokenId The ID of the minted token
     */
    function mint(
        address to,
        uint256 dealId,
        uint256 depositAmount
    ) external returns (uint256 tokenId) {
        if (!authorizedVaults[msg.sender]) revert UnauthorizedVault();
        if (to == address(0)) revert InvalidRecipient();

        tokenId = _nextTokenId++;

        positions[tokenId] = Position({
            dealId: dealId,
            depositAmount: depositAmount,
            depositTime: block.timestamp,
            claimed: false
        });

        _safeMint(to, tokenId);

        emit PositionMinted(tokenId, to, dealId, depositAmount);
    }

    /**
     * @notice Mark a position as claimed without burning
     * @param tokenId Token ID to mark as claimed
     * @dev Used when position is claimed but NFT kept as receipt
     */
    function markClaimed(uint256 tokenId) external {
        if (!authorizedVaults[msg.sender]) revert UnauthorizedVault();
        if (positions[tokenId].claimed) revert PositionAlreadyClaimed();

        positions[tokenId].claimed = true;
    }

    /**
     * @notice Burn a position NFT when claimed
     * @param tokenId Token ID to burn
     */
    function burn(uint256 tokenId) external {
        if (!authorizedVaults[msg.sender]) revert UnauthorizedVault();
        if (positions[tokenId].claimed) revert PositionAlreadyClaimed();

        positions[tokenId].claimed = true;
        _burn(tokenId);

        emit PositionBurned(tokenId, ownerOf(tokenId));
    }

    /**
     * @notice Authorize or deauthorize a vault to mint/burn positions
     * @param vault Address of the vault
     * @param authorized Whether to authorize or deauthorize
     */
    function setVaultAuthorization(address vault, bool authorized) external onlyOwner {
        authorizedVaults[vault] = authorized;
        emit VaultAuthorized(vault, authorized);
    }

    /**
     * @notice Get position data
     * @param tokenId Token ID to query
     * @return Position data
     */
    function getPosition(uint256 tokenId) external view returns (Position memory) {
        return positions[tokenId];
    }

    /**
     * @notice Get all positions owned by an address
     * @param owner Address to query
     * @return tokenIds Array of token IDs owned by the address
     */
    function getPositionsByOwner(address owner) external view returns (uint256[] memory tokenIds) {
        uint256 balance = balanceOf(owner);
        tokenIds = new uint256[](balance);

        for (uint256 i = 0; i < balance; i++) {
            tokenIds[i] = tokenOfOwnerByIndex(owner, i);
        }
    }

    /**
     * @notice Override required by Solidity for multiple inheritance
     */
    function _update(address to, uint256 tokenId, address auth)
        internal
        override(ERC721, ERC721Enumerable)
        returns (address)
    {
        return super._update(to, tokenId, auth);
    }

    /**
     * @notice Override required by Solidity for multiple inheritance
     */
    function _increaseBalance(address account, uint128 value)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._increaseBalance(account, value);
    }

    /**
     * @notice Override required by Solidity for multiple inheritance
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

project/contracts/core/DealVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./DealPosition.sol";
import "./ChannelRegistry.sol";
import "../integrations/flare/FlarePriceReader.sol";

/**
 * @title DealVault
 * @notice Main vault for LP deposits with state channel integration
 * @dev Integrates Yellow/Nitrolite channels, Flare FTSO pricing, and position NFTs
 */
contract DealVault is Ownable2Step, ReentrancyGuard {
    using SafeERC20 for IERC20;

    /// @notice Deal status
    enum DealStatus {
        Active,
        Locked,        // Deal locked, no new deposits allowed
        Settling,      // Settlement in progress with channel proof
        Finalized,
        Cancelled
    }

    /// @notice Deal parameters
    struct Deal {
        uint256 dealId;                // Unique deal identifier
        address depositToken;          // Token LPs deposit
        address targetToken;           // Token for trading/lending
        uint256 targetChainId;         // Target chain for operations
        uint256 minDeposit;            // Minimum deposit amount
        uint256 maxDeposit;            // Maximum deposit amount
        uint256 totalDeposited;        // Total amount deposited
        uint256 startTime;             // Deal start timestamp
        uint256 duration;              // Deal duration in seconds
        DealStatus status;             // Current deal status
        uint256 expectedYield;         // Expected yield in basis points
        bytes32 channelId;             // Associated state channel ID
    }

    /// @notice User deposit tracking for reward distribution
    struct UserDeposit {
        uint256 amount;                // Amount deposited by user
        address yellowAddress;         // User's Yellow Network trading address
        uint256 rewardShare;           // Calculated reward amount
        bool rewardsClaimed;           // Whether rewards have been claimed
    }

    /// @notice Position NFT contract
    DealPosition public immutable positionNFT;

    /// @notice Channel registry
    ChannelRegistry public immutable channelRegistry;

    /// @notice Price reader
    FlarePriceReader public immutable priceReader;

    /// @notice Counter for deal IDs
    uint256 private _nextDealId;

    /// @notice Mapping from deal ID to deal data
    mapping(uint256 => Deal) public deals;

    /// @notice Mapping from position token ID to deal ID
    mapping(uint256 => uint256) public positionDeals;

    /// @notice Mapping from token address to FTSO symbol
    mapping(address => string) public tokenSymbols;

    /// @notice Mapping from deal ID to user address to user deposit data
    mapping(uint256 => mapping(address => UserDeposit)) public userDeposits;

    /// @notice Array of depositors per deal for enumeration
    mapping(uint256 => address[]) public dealDepositors;

    /// @notice External protocol address for yield generation
    address public protocolAddress;

    /// @notice Protocol fee in basis points (100 = 1%)
    uint256 public protocolFeeBps = 100;

    /// @notice Fee recipient
    address public feeRecipient;

    /// @notice Minimum deal duration (1 day)
    uint256 public constant MIN_DEAL_DURATION = 1 days;

    /// @notice Maximum deal duration (365 days)
    uint256 public constant MAX_DEAL_DURATION = 365 days;

    /// @notice Events
    event DealCreated(
        uint256 indexed dealId,
        address depositToken,
        address targetToken,
        uint256 targetChainId,
        uint256 duration
    );
    event DealLocked(uint256 indexed dealId, bytes32 indexed channelId);
    event DealSettling(uint256 indexed dealId, uint256 finalPnL);
    event Deposited(
        uint256 indexed dealId,
        address indexed depositor,
        uint256 indexed positionId,
        uint256 amount
    );
    event PositionClaimed(
        uint256 indexed dealId,
        uint256 indexed positionId,
        address indexed recipient,
        uint256 principal,
        uint256 yield
    );
    event Withdrawn(
        uint256 indexed dealId,
        uint256 indexed positionId,
        address indexed recipient,
        uint256 amount,
        uint256 yield
    );
    event DealFinalized(uint256 indexed dealId, uint256 totalYield);
    event DealCancelled(uint256 indexed dealId);
    event ProtocolFeeUpdated(uint256 newFeeBps);
    event FeeRecipientUpdated(address indexed newRecipient);
    event ProtocolAddressUpdated(address indexed newProtocol);
    event DepositedToProtocol(
        uint256 indexed dealId,
        address indexed protocol,
        address token,
        uint256 amount
    );
    event RewardsClaimedFromProtocol(
        uint256 indexed dealId,
        address indexed protocol,
        uint256 rewards
    );
    event UserYellowAddressUpdated(
        uint256 indexed dealId,
        address indexed user,
        address indexed yellowAddress
    );
    event RewardsDistributedToYellow(
        uint256 indexed dealId,
        uint256 totalRewards,
        uint256 userCount
    );

    /// @notice Custom errors
    error InvalidDeal();
    error DealNotActive();
    error DealNotLocked();
    error DealNotSettling();
    error DealNotFinalized();
    error InvalidDepositAmount();
    error InvalidDuration();
    error InvalidPosition();
    error InvalidChannelProof();
    error PositionAlreadyClaimed();
    error DealStillActive();
    error UnauthorizedWithdrawal();
    error ChannelNotLinked();
    error ProtocolNotSet();
    error NoRewardsToClaim();
    error RewardsAlreadyClaimed();

    /**
     * @notice Constructor
     * @param initialOwner Address that will own the contract
     * @param _positionNFT Position NFT contract address
     * @param _channelRegistry Channel registry address
     * @param _priceReader Price reader address
     * @param _feeRecipient Initial fee recipient
     */
    constructor(
        address initialOwner,
        address _positionNFT,
        address _channelRegistry,
        address _priceReader,
        address _feeRecipient
    ) Ownable(initialOwner) {
        positionNFT = DealPosition(_positionNFT);
        channelRegistry = ChannelRegistry(_channelRegistry);
        priceReader = FlarePriceReader(_priceReader);
        feeRecipient = _feeRecipient;
        _nextDealId = 1;
    }

    /**
     * @notice Create a new deal
     * @param depositToken Token LPs will deposit
     * @param targetToken Token for operations
     * @param targetChainId Target chain ID
     * @param minDeposit Minimum deposit amount
     * @param maxDeposit Maximum deposit amount
     * @param duration Deal duration in seconds
     * @param expectedYield Expected yield in basis points
     * @return dealId The created deal ID
     */
    function createDeal(
        address depositToken,
        address targetToken,
        uint256 targetChainId,
        uint256 minDeposit,
        uint256 maxDeposit,
        uint256 duration,
        uint256 expectedYield
    ) external onlyOwner returns (uint256 dealId) {
        if (duration < MIN_DEAL_DURATION || duration > MAX_DEAL_DURATION) {
            revert InvalidDuration();
        }
        if (minDeposit == 0 || maxDeposit < minDeposit) {
            revert InvalidDepositAmount();
        }

        dealId = _nextDealId++;

        deals[dealId] = Deal({
            dealId: dealId,
            depositToken: depositToken,
            targetToken: targetToken,
            targetChainId: targetChainId,
            minDeposit: minDeposit,
            maxDeposit: maxDeposit,
            totalDeposited: 0,
            startTime: block.timestamp,
            duration: duration,
            status: DealStatus.Active,
            expectedYield: expectedYield,
            channelId: bytes32(0)
        });

        emit DealCreated(dealId, depositToken, targetToken, targetChainId, duration);
    }

    /**
     * @notice Deposit into a deal
     * @param dealId Deal identifier
     * @param amount Amount to deposit
     * @return positionId Minted position NFT ID
     */
    function deposit(uint256 dealId, uint256 amount)
        external
        nonReentrant
        returns (uint256 positionId)
    {
        Deal storage deal = deals[dealId];

        if (deal.status != DealStatus.Active) revert DealNotActive();
        if (amount < deal.minDeposit || amount > deal.maxDeposit) {
            revert InvalidDepositAmount();
        }
        if (block.timestamp >= deal.startTime + deal.duration) {
            revert DealNotActive();
        }

        // Transfer tokens
        IERC20(deal.depositToken).safeTransferFrom(msg.sender, address(this), amount);

        // Update total deposited
        deal.totalDeposited += amount;

        // Track user deposit for reward distribution
        UserDeposit storage userDep = userDeposits[dealId][msg.sender];
        if (userDep.amount == 0) {
            // First deposit from this user - add to depositors array
            dealDepositors[dealId].push(msg.sender);
            userDep.yellowAddress = msg.sender; // Default to same address
        }
        userDep.amount += amount;

        // Mint position NFT
        positionId = positionNFT.mint(msg.sender, dealId, amount);
        positionDeals[positionId] = dealId;

        emit Deposited(dealId, msg.sender, positionId, amount);
    }

    /**
     * @notice Withdraw from a finalized deal
     * @param positionId Position NFT ID
     */
    function withdraw(uint256 positionId) external nonReentrant {
        // Verify ownership
        if (positionNFT.ownerOf(positionId) != msg.sender) {
            revert UnauthorizedWithdrawal();
        }

        uint256 dealId = positionDeals[positionId];
        Deal storage deal = deals[dealId];

        if (deal.status != DealStatus.Finalized) revert DealNotFinalized();

        // Get position data
        DealPosition.Position memory position = positionNFT.getPosition(positionId);
        if (position.claimed) revert PositionAlreadyClaimed();

        // Calculate yield (simplified - in production would use actual returns)
        uint256 yieldAmount = (position.depositAmount * deal.expectedYield) / 10000;

        // Calculate protocol fee
        uint256 fee = (yieldAmount * protocolFeeBps) / 10000;
        uint256 netYield = yieldAmount - fee;

        // Total to withdraw
        uint256 totalAmount = position.depositAmount + netYield;

        // Burn position NFT
        positionNFT.burn(positionId);

        // Transfer tokens
        IERC20(deal.depositToken).safeTransfer(msg.sender, totalAmount);

        if (fee > 0) {
            IERC20(deal.depositToken).safeTransfer(feeRecipient, fee);
        }

        emit Withdrawn(dealId, positionId, msg.sender, position.depositAmount, netYield);
    }

    /**
     * @notice Lock a deal to prevent new deposits
     * @param dealId Deal identifier
     * @dev Opens Yellow channel and transitions to Locked state
     */
    function lockDeal(uint256 dealId) external onlyOwner {
        Deal storage deal = deals[dealId];

        if (deal.status != DealStatus.Active) revert DealNotActive();
        if (deal.channelId == bytes32(0)) revert ChannelNotLinked();

        // Transition to Locked state
        deal.status = DealStatus.Locked;

        emit DealLocked(dealId, deal.channelId);
    }

    /**
     * @notice Settle a deal with channel proof and compute final PnL
     * @param dealId Deal identifier
     * @param channelProof Proof from Yellow channel settlement
     * @dev Validates channel proof, computes PnL using FTSO, transitions to Settling
     */
    function settleDeal(uint256 dealId, bytes calldata channelProof) external onlyOwner {
        Deal storage deal = deals[dealId];

        if (deal.status != DealStatus.Locked) revert DealNotLocked();
        if (deal.channelId == bytes32(0)) revert ChannelNotLinked();

        // Validate channel proof (simplified - in production would verify signatures)
        if (channelProof.length < 32) revert InvalidChannelProof();

        // Compute final PnL using FTSO prices
        uint256 finalPnL = _computeFinalPnL(dealId);

        // Transition to Settling state
        deal.status = DealStatus.Settling;

        emit DealSettling(dealId, finalPnL);
    }

    /**
     * @notice Claim position returns after settlement
     * @param positionId Position NFT ID
     * @dev Separate from withdraw - allows claiming without burning NFT immediately
     */
    function claimPosition(uint256 positionId) external nonReentrant {
        // Verify ownership
        if (positionNFT.ownerOf(positionId) != msg.sender) {
            revert UnauthorizedWithdrawal();
        }

        uint256 dealId = positionDeals[positionId];
        Deal storage deal = deals[dealId];

        if (deal.status != DealStatus.Settling && deal.status != DealStatus.Finalized) {
            revert DealNotSettling();
        }

        // Get position data
        DealPosition.Position memory position = positionNFT.getPosition(positionId);
        if (position.claimed) revert PositionAlreadyClaimed();

        // Compute actual PnL using FTSO prices
        uint256 yieldAmount = _computePositionYield(dealId, position.depositAmount);

        // Calculate protocol fee
        uint256 fee = (yieldAmount * protocolFeeBps) / 10000;
        uint256 netYield = yieldAmount - fee;

        // Total to claim
        uint256 totalAmount = position.depositAmount + netYield;

        // Mark as claimed (but don't burn NFT yet)
        positionNFT.markClaimed(positionId);

        // Transfer tokens
        IERC20(deal.depositToken).safeTransfer(msg.sender, totalAmount);

        if (fee > 0) {
            IERC20(deal.depositToken).safeTransfer(feeRecipient, fee);
        }

        emit PositionClaimed(dealId, positionId, msg.sender, position.depositAmount, netYield);
    }

    /**
     * @notice Set FTSO symbol for token address
     * @param tokenAddress Token contract address
     * @param symbol FTSO symbol (e.g., "USDC", "BTC", "ETH")
     */
    function setTokenSymbol(address tokenAddress, string memory symbol) external onlyOwner {
        tokenSymbols[tokenAddress] = symbol;
    }

    /**
     * @notice Batch set token symbols
     * @param tokenAddresses Array of token addresses
     * @param symbols Array of FTSO symbols
     */
    function setTokenSymbols(
        address[] memory tokenAddresses,
        string[] memory symbols
    ) external onlyOwner {
        require(tokenAddresses.length == symbols.length, "Length mismatch");
        for (uint256 i = 0; i < tokenAddresses.length; i++) {
            tokenSymbols[tokenAddresses[i]] = symbols[i];
        }
    }

    /**
     * @notice Compute final PnL using FTSO price feeds
     * @param dealId Deal identifier
     * @return Final PnL in basis points
     */
    function _computeFinalPnL(uint256 dealId) internal view returns (uint256) {
        Deal storage deal = deals[dealId];

        // Get FTSO symbols for tokens
        string memory depositSymbol = tokenSymbols[deal.depositToken];
        string memory targetSymbol = tokenSymbols[deal.targetToken];

        // If symbols not configured, use expected yield
        if (bytes(depositSymbol).length == 0 || bytes(targetSymbol).length == 0) {
            return deal.expectedYield;
        }

        // Get price ratio from FTSO
        try priceReader.getPriceRatio(depositSymbol, targetSymbol) returns (
            uint256 ratio,
            uint256 /* timestamp */
        ) {
            // Calculate actual yield based on price movement
            // ratio > 1e18 means depositToken gained value vs targetToken
            if (ratio > 1e18) {
                return ((ratio - 1e18) * 10000) / 1e18; // Convert to basis points
            }
            // If ratio <= 1e18, no positive yield
            return 0;
        } catch {
            // Fallback to expected yield if price feed fails
            return deal.expectedYield;
        }
    }

    /**
     * @notice Compute position-specific yield
     * @param dealId Deal identifier
     * @param depositAmount Position deposit amount
     * @return Yield amount in deposit token
     */
    function _computePositionYield(uint256 dealId, uint256 depositAmount) internal view returns (uint256) {
        uint256 finalPnL = _computeFinalPnL(dealId);
        return (depositAmount * finalPnL) / 10000;
    }

    /**
     * @notice Finalize a deal
     * @param dealId Deal identifier
     */
    function finalizeDeal(uint256 dealId) external onlyOwner {
        Deal storage deal = deals[dealId];

        if (deal.status != DealStatus.Active) revert InvalidDeal();
        if (block.timestamp < deal.startTime + deal.duration) {
            revert DealStillActive();
        }

        deal.status = DealStatus.Finalized;

        emit DealFinalized(dealId, 0); // TODO: Calculate actual yield
    }

    /**
     * @notice Cancel a deal (emergency only)
     * @param dealId Deal identifier
     */
    function cancelDeal(uint256 dealId) external onlyOwner {
        Deal storage deal = deals[dealId];

        if (deal.status != DealStatus.Active) revert InvalidDeal();

        deal.status = DealStatus.Cancelled;

        emit DealCancelled(dealId);
    }

    /**
     * @notice Link deal to state channel
     * @param dealId Deal identifier
     * @param channelId Channel identifier
     */
    function linkChannel(uint256 dealId, bytes32 channelId) external onlyOwner {
        Deal storage deal = deals[dealId];
        if (deal.status != DealStatus.Active) revert DealNotActive();

        deal.channelId = channelId;
    }

    /**
     * @notice Update protocol fee
     * @param newFeeBps New fee in basis points
     */
    function setProtocolFee(uint256 newFeeBps) external onlyOwner {
        require(newFeeBps <= 1000, "Fee too high"); // Max 10%
        protocolFeeBps = newFeeBps;
        emit ProtocolFeeUpdated(newFeeBps);
    }

    /**
     * @notice Update fee recipient
     * @param newRecipient New fee recipient address
     */
    function setFeeRecipient(address newRecipient) external onlyOwner {
        require(newRecipient != address(0), "Invalid recipient");
        feeRecipient = newRecipient;
        emit FeeRecipientUpdated(newRecipient);
    }

    /**
     * @notice Set protocol address for yield generation
     * @param newProtocol Protocol contract address
     */
    function setProtocolAddress(address newProtocol) external onlyOwner {
        require(newProtocol != address(0), "Invalid protocol");
        protocolAddress = newProtocol;
        emit ProtocolAddressUpdated(newProtocol);
    }

    /**
     * @notice Set user's Yellow Network trading address
     * @param dealId Deal identifier
     * @param yellowAddress Yellow Network address for receiving rewards
     */
    function setUserYellowAddress(uint256 dealId, address yellowAddress) external {
        require(yellowAddress != address(0), "Invalid address");

        UserDeposit storage userDep = userDeposits[dealId][msg.sender];
        require(userDep.amount > 0, "No deposit found");

        userDep.yellowAddress = yellowAddress;

        emit UserYellowAddressUpdated(dealId, msg.sender, yellowAddress);
    }

    /**
     * @notice Deposit all deal funds to external protocol
     * @param dealId Deal identifier
     */
    function depositToProtocol(uint256 dealId) external onlyOwner nonReentrant {
        Deal storage deal = deals[dealId];

        if (deal.status != DealStatus.Locked) revert DealNotLocked();
        if (protocolAddress == address(0)) revert ProtocolNotSet();

        uint256 amount = deal.totalDeposited;
        require(amount > 0, "No funds to deposit");

        // Approve protocol to spend tokens
        IERC20(deal.depositToken).approve(protocolAddress, amount);

        // Call protocol's deposit function
        (bool success,) = protocolAddress.call(
            abi.encodeWithSignature(
                "deposit(address,uint256)",
                deal.depositToken,
                amount
            )
        );
        require(success, "Protocol deposit failed");

        emit DepositedToProtocol(dealId, protocolAddress, deal.depositToken, amount);
    }

    /**
     * @notice Claim rewards from external protocol
     * @param dealId Deal identifier
     * @return rewards Amount of rewards claimed
     */
    function claimRewardsFromProtocol(uint256 dealId)
        external
        onlyOwner
        nonReentrant
        returns (uint256 rewards)
    {
        Deal storage deal = deals[dealId];

        if (deal.status != DealStatus.Locked && deal.status != DealStatus.Settling) {
            revert InvalidDeal();
        }
        if (protocolAddress == address(0)) revert ProtocolNotSet();

        // Get balance before
        uint256 balanceBefore = IERC20(deal.depositToken).balanceOf(address(this));

        // Call protocol's claimRewards function
        (bool success,) = protocolAddress.call(
            abi.encodeWithSignature(
                "claimRewards(address)",
                deal.depositToken
            )
        );
        require(success, "Protocol claim failed");

        // Calculate rewards received
        uint256 balanceAfter = IERC20(deal.depositToken).balanceOf(address(this));
        rewards = balanceAfter - balanceBefore;

        if (rewards == 0) revert NoRewardsToClaim();

        // Calculate each user's proportional share
        address[] storage depositors = dealDepositors[dealId];
        for (uint256 i = 0; i < depositors.length; i++) {
            address user = depositors[i];
            UserDeposit storage userDep = userDeposits[dealId][user];

            // Proportional share: (userAmount / totalDeposited) * rewards
            userDep.rewardShare = (userDep.amount * rewards) / deal.totalDeposited;
        }

        emit RewardsClaimedFromProtocol(dealId, protocolAddress, rewards);
    }

    /**
     * @notice Get user's deposit information
     * @param dealId Deal identifier
     * @param user User address
     * @return User deposit data
     */
    function getUserDeposit(uint256 dealId, address user)
        external
        view
        returns (UserDeposit memory)
    {
        return userDeposits[dealId][user];
    }

    /**
     * @notice Get all depositors for a deal
     * @param dealId Deal identifier
     * @return Array of depositor addresses
     */
    function getDealDepositors(uint256 dealId)
        external
        view
        returns (address[] memory)
    {
        return dealDepositors[dealId];
    }

    /**
     * @notice Get deal information
     * @param dealId Deal identifier
     * @return Deal data
     */
    function getDeal(uint256 dealId) external view returns (Deal memory) {
        return deals[dealId];
    }

    /**
     * @notice Check if deal is active
     * @param dealId Deal identifier
     * @return True if deal is active
     */
    function isDealActive(uint256 dealId) external view returns (bool) {
        Deal storage deal = deals[dealId];
        return deal.status == DealStatus.Active && 
               block.timestamp < deal.startTime + deal.duration;
    }

    /**
     * @notice Get expected return for a position
     * @param positionId Position NFT ID
     * @return Expected total return (principal + yield)
     */
    function getExpectedReturn(uint256 positionId) external view returns (uint256) {
        uint256 dealId = positionDeals[positionId];
        Deal storage deal = deals[dealId];

        DealPosition.Position memory position = positionNFT.getPosition(positionId);

        uint256 yieldAmount = (position.depositAmount * deal.expectedYield) / 10000;
        uint256 fee = (yieldAmount * protocolFeeBps) / 10000;

        return position.depositAmount + yieldAmount - fee;
    }
}

