// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/access/Ownable2Step.sol";
import "../../core/DealVault.sol";
import "./YellowChannel.sol";

/**
 * @title NitroliteAdapter
 * @notice Bridge between Liquium deals and Yellow/Nitrolite state channels
 * @dev Adapts DealVault operations to YellowChannel ERC-7824 format
 * 
 * Architecture:
 * DealVault → NitroliteAdapter → YellowChannel → Nitrolite SDK
 * 
 * Key Responsibilities:
 * - Map deals to channels
 * - Transform deal states to channel states
 * - Generate settlement proofs
 * - Coordinate lifecycle transitions
 */
contract NitroliteAdapter is Ownable2Step {
    /// @notice Deal vault reference
    DealVault public immutable dealVault;

    /// @notice Yellow channel reference
    YellowChannel public immutable yellowChannel;

    /// @notice Mapping from deal ID to channel ID
    mapping(uint256 => bytes32) public dealToChannel;

    /// @notice Mapping from channel ID to deal ID
    mapping(bytes32 => uint256) public channelToDeal;

    /// @notice Channel state data
    struct ChannelState {
        bytes32 channelId;
        uint256 dealId;
        uint256 nonce;
        bytes32 latestStateHash;
        bool active;
    }

    /// @notice Mapping from channel ID to state
    mapping(bytes32 => ChannelState) public channelStates;

    /// @notice Events
    event ChannelCreatedForDeal(
        uint256 indexed dealId,
        bytes32 indexed channelId,
        address party0,
        address party1
    );
    event StateUpdateProcessed(
        bytes32 indexed channelId,
        uint256 nonce,
        bytes32 stateHash
    );
    event SettlementProofGenerated(
        bytes32 indexed channelId,
        uint256 indexed dealId,
        bytes proof
    );
    event ChannelSettled(bytes32 indexed channelId, uint256 indexed dealId);

    /// @notice Custom errors
    error ChannelAlreadyExists();
    error ChannelNotFound();
    error InvalidDeal();
    error InvalidState();

    /**
     * @notice Constructor
     * @param initialOwner Address that will own the contract
     * @param _dealVault Address of DealVault contract
     * @param _yellowChannel Address of YellowChannel contract
     */
    constructor(
        address initialOwner,
        address _dealVault,
        address _yellowChannel
    ) Ownable(initialOwner) {
        dealVault = DealVault(_dealVault);
        yellowChannel = YellowChannel(_yellowChannel);
    }

    /**
     * @notice Create channel for a deal
     * @param dealId Deal identifier
     * @param party0 First party address (dealer)
     * @param party1 Second party address (LP)
     * @param initialBalance0 Initial balance for party0
     * @param initialBalance1 Initial balance for party1
     * @return channelId Created channel identifier
     */
    function createChannelForDeal(
        uint256 dealId,
        address party0,
        address party1,
        uint256 initialBalance0,
        uint256 initialBalance1
    ) external onlyOwner returns (bytes32 channelId) {
        // Check deal exists and is valid
        DealVault.Deal memory deal = dealVault.getDeal(dealId);
        if (deal.dealId == 0) revert InvalidDeal();

        // Generate unique channel ID
        channelId = keccak256(
            abi.encodePacked(
                dealId,
                party0,
                party1,
                block.timestamp
            )
        );

        // Check no existing channel
        if (dealToChannel[dealId] != bytes32(0)) revert ChannelAlreadyExists();

        // Open Yellow channel
        yellowChannel.openYellowChannel(
            channelId,
            party0,
            party1,
            initialBalance0,
            initialBalance1,
            dealId
        );

        // Create bidirectional mapping
        dealToChannel[dealId] = channelId;
        channelToDeal[channelId] = dealId;

        // Initialize channel state
        channelStates[channelId] = ChannelState({
            channelId: channelId,
            dealId: dealId,
            nonce: 0,
            latestStateHash: keccak256(
                abi.encodePacked(initialBalance0, initialBalance1, uint256(0))
            ),
            active: true
        });

        // Link channel to deal in vault
        dealVault.linkChannel(dealId, channelId);

        emit ChannelCreatedForDeal(dealId, channelId, party0, party1);
    }

    /**
     * @notice Process state update from Nitrolite
     * @param channelId Channel identifier
     * @param newStateHash New state hash
     * @param newNonce New nonce
     * @param balance0 New balance for party0
     * @param balance1 New balance for party1
     * @param signature0 Signature from party0
     * @param signature1 Signature from party1
     */
    function processStateUpdate(
        bytes32 channelId,
        bytes32 newStateHash,
        uint256 newNonce,
        uint256 balance0,
        uint256 balance1,
        bytes calldata signature0,
        bytes calldata signature1
    ) external onlyOwner {
        ChannelState storage state = channelStates[channelId];

        if (!state.active) revert ChannelNotFound();
        if (newNonce <= state.nonce) revert InvalidState();

        // Commit state to Yellow channel
        yellowChannel.commitState(
            channelId,
            newStateHash,
            newNonce,
            balance0,
            balance1,
            signature0,
            signature1
        );

        // Update local state
        state.nonce = newNonce;
        state.latestStateHash = newStateHash;

        emit StateUpdateProcessed(channelId, newNonce, newStateHash);
    }

    /**
     * @notice Generate settlement proof for deal
     * @param channelId Channel identifier
     * @return proof Settlement proof bytes
     */
    function generateSettlementProof(bytes32 channelId)
        public
        view
        returns (bytes memory proof)
    {
        ChannelState storage state = channelStates[channelId];
        if (!state.active) revert ChannelNotFound();

        // Get channel commitment
        YellowChannel.ChannelCommitment memory commitment = 
            yellowChannel.getCommitment(channelId);

        // Encode proof with all necessary data
        proof = abi.encode(
            channelId,
            state.dealId,
            commitment.nonce,
            commitment.stateHash,
            commitment.balance0,
            commitment.balance1,
            commitment.signature0,
            commitment.signature1
        );
    }

    /**
     * @notice Process settlement for deal with channel proof
     * @param dealId Deal identifier
     */
    function processSettlement(uint256 dealId) external onlyOwner {
        bytes32 channelId = dealToChannel[dealId];
        if (channelId == bytes32(0)) revert ChannelNotFound();

        ChannelState storage state = channelStates[channelId];
        if (!state.active) revert InvalidState();

        // Generate settlement proof
        bytes memory proof = this.generateSettlementProof(channelId);

        // Settle channel in Yellow
        yellowChannel.settleChannel(channelId);

        // Settle deal in vault with proof
        dealVault.settleDeal(dealId, proof);

        // Mark channel as inactive
        state.active = false;

        emit ChannelSettled(channelId, dealId);
    }

    /**
     * @notice Get channel ID for deal
     * @param dealId Deal identifier
     * @return channelId Associated channel ID
     */
    function getChannelForDeal(uint256 dealId) 
        external 
        view 
        returns (bytes32 channelId) 
    {
        return dealToChannel[dealId];
    }

    /**
     * @notice Get deal ID for channel
     * @param channelId Channel identifier
     * @return dealId Associated deal ID
     */
    function getDealForChannel(bytes32 channelId) 
        external 
        view 
        returns (uint256 dealId) 
    {
        return channelToDeal[channelId];
    }

    /**
     * @notice Get channel state
     * @param channelId Channel identifier
     * @return Channel state data
     */
    function getChannelState(bytes32 channelId) 
        external 
        view 
        returns (ChannelState memory) 
    {
        return channelStates[channelId];
    }

    /**
     * @notice Check if channel is active
     * @param channelId Channel identifier
     * @return True if channel is active
     */
    function isChannelActive(bytes32 channelId) 
        external 
        view 
        returns (bool) 
    {
        return channelStates[channelId].active;
    }
}

project/contracts/integrations/yellow/Ye.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/access/Ownable2Step.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";
import "../../core/ChannelRegistry.sol";

/**
 * @title YellowChannel
 * @notice Integration with Yellow Network's Nitrolite Protocol (ERC-7824)
 * @dev Extends channel functionality with Yellow's state channel primitives
 * 
 * Yellow Network Integration:
 * - Uses Nitrolite for off-chain state updates
 * - Implements ERC-7824 standard for state channels
 * - Enables high-frequency trading without L1 settlement
 * 
 * Key Features:
 * - Non-custodial: Users retain control of funds
 * - Instant settlement: State updates happen off-chain
 * - Cross-chain: Can bridge between different networks
 * - Scalable: Unlimited throughput via channels
 */
contract YellowChannel is Ownable2Step {
    using SafeERC20 for IERC20;
    using ECDSA for bytes32;
    using MessageHashUtils for bytes32;

    /// @notice Channel registry for state management
    ChannelRegistry public immutable channelRegistry;

    /// @notice Yellow node WebSocket endpoint
    string public yellowNodeEndpoint;

    /// @notice Application ID in Yellow network
    bytes32 public yellowAppId;

    /// @notice Channel commitment data
    struct ChannelCommitment {
        bytes32 channelId;
        bytes32 stateHash;          // Hash of current state
        uint256 nonce;              // State update nonce
        uint256 balance0;           // Party 0 balance
        uint256 balance1;           // Party 1 balance
        bytes signature0;           // Party 0 signature
        bytes signature1;           // Party 1 signature
        uint256 timeout;            // Timeout for disputes
    }

    /// @notice Channel custody data (AssetHolder pattern)
    struct ChannelAssets {
        address party0;             // First party
        address party1;             // Second party
        address token;              // Asset token
        uint256 totalDeposited;     // Total deposited
        bool settled;               // Settlement complete
        uint256 challengeDeadline;  // Challenge window end
    }

    /// @notice Mapping from channel ID to commitment
    mapping(bytes32 => ChannelCommitment) public commitments;

    /// @notice Mapping from channel ID to assets (AssetHolder)
    mapping(bytes32 => ChannelAssets) public channelAssets;

    /// @notice Challenge period duration (1 day)
    uint256 public constant CHALLENGE_PERIOD = 1 days;

    /// @notice Events
    event YellowChannelOpened(
        bytes32 indexed channelId,
        address party0,
        address party1,
        uint256 amount0,
        uint256 amount1
    );
    event StateCommitted(
        bytes32 indexed channelId,
        bytes32 stateHash,
        uint256 nonce
    );
    event ChannelSettled(
        bytes32 indexed channelId,
        uint256 finalBalance0,
        uint256 finalBalance1
    );
    event YellowConfigUpdated(string endpoint, bytes32 appId);

    /// @notice Custom errors
    error InvalidCommitment();
    error InvalidSignature();
    error CommitmentTooOld();
    error DisputeTimeout();

    /**
     * @notice Constructor
     * @param initialOwner Address that will own the contract
     * @param _channelRegistry Address of channel registry
     * @param _yellowNodeEndpoint Yellow node WebSocket endpoint
     * @param _yellowAppId Application ID in Yellow network
     */
    constructor(
        address initialOwner,
        address _channelRegistry,
        string memory _yellowNodeEndpoint,
        bytes32 _yellowAppId
    ) Ownable(initialOwner) {
        channelRegistry = ChannelRegistry(_channelRegistry);
        yellowNodeEndpoint = _yellowNodeEndpoint;
        yellowAppId = _yellowAppId;
    }

    /**
     * @notice Open a new Yellow state channel
     * @param channelId Unique channel identifier
     * @param party0 First party address
     * @param party1 Second party address
     * @param amount0 Initial balance for party0
     * @param amount1 Initial balance for party1
     * @param dealId Associated deal ID
     */
    function openYellowChannel(
        bytes32 channelId,
        address party0,
        address party1,
        uint256 amount0,
        uint256 amount1,
        uint256 dealId
    ) external onlyOwner {
        // Open channel in registry
        channelRegistry.openChannel(
            channelId,
            address(this),  // dealer
            party1,         // counterparty
            dealId,
            amount0 + amount1
        );

        // Initialize commitment
        commitments[channelId] = ChannelCommitment({
            channelId: channelId,
            stateHash: keccak256(abi.encodePacked(amount0, amount1, uint256(0))),
            nonce: 0,
            balance0: amount0,
            balance1: amount1,
            signature0: "",
            signature1: "",
            timeout: block.timestamp + 7 days
        });

        emit YellowChannelOpened(channelId, party0, party1, amount0, amount1);
    }

    /**
     * @notice Deposit assets to channel (AssetHolder pattern)
     * @param channelId Channel identifier
     * @param token Token address
     * @param amount Amount to deposit
     */
    function depositToChannel(
        bytes32 channelId,
        address token,
        uint256 amount
    ) external onlyOwner {
        ChannelAssets storage assets = channelAssets[channelId];

        // Transfer tokens to this contract (custody)
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        assets.token = token;
        assets.totalDeposited += amount;
    }

    /**
     * @notice Verify dual signatures for state update
     * @param stateHash Hash of the state being signed
     * @param party0 Address of first party
     * @param party1 Address of second party
     * @param signature0 Signature from party0
     * @param signature1 Signature from party1
     * @return True if both signatures are valid
     */
    function verifySignatures(
        bytes32 stateHash,
        address party0,
        address party1,
        bytes memory signature0,
        bytes memory signature1
    ) public pure returns (bool) {
        bytes32 ethSignedHash = stateHash.toEthSignedMessageHash();

        address signer0 = ethSignedHash.recover(signature0);
        address signer1 = ethSignedHash.recover(signature1);

        return signer0 == party0 && signer1 == party1;
    }

    /**
     * @notice Commit new state to channel
     * @param channelId Channel identifier
     * @param newStateHash New state hash
     * @param newNonce New nonce (must be higher than current)
     * @param newBalance0 New balance for party0
     * @param newBalance1 New balance for party1
     * @param signature0 Signature from party0
     * @param signature1 Signature from party1
     */
    function commitState(
        bytes32 channelId,
        bytes32 newStateHash,
        uint256 newNonce,
        uint256 newBalance0,
        uint256 newBalance1,
        bytes calldata signature0,
        bytes calldata signature1
    ) external {
        ChannelCommitment storage commitment = commitments[channelId];
        ChannelAssets storage assets = channelAssets[channelId];

        if (newNonce <= commitment.nonce) revert CommitmentTooOld();

        // Verify dual signatures
        if (!verifySignatures(newStateHash, assets.party0, assets.party1, signature0, signature1)) {
            revert InvalidSignature();
        }

        // Update commitment
        commitment.stateHash = newStateHash;
        commitment.nonce = newNonce;
        commitment.balance0 = newBalance0;
        commitment.balance1 = newBalance1;
        commitment.signature0 = signature0;
        commitment.signature1 = signature1;

        // Update channel registry
        channelRegistry.updateChannelState(channelId, newStateHash, newNonce);

        emit StateCommitted(channelId, newStateHash, newNonce);
    }

    /**
     * @notice Withdraw from channel after settlement
     * @param channelId Channel identifier
     * @param party Address to withdraw to
     * @param amount Amount to withdraw
     */
    function withdrawFromChannel(
        bytes32 channelId,
        address party,
        uint256 amount
    ) external onlyOwner {
        ChannelAssets storage assets = channelAssets[channelId];

        require(assets.settled, "Channel not settled");
        require(block.timestamp >= assets.challengeDeadline, "Challenge period active");

        // Transfer tokens from custody
        IERC20(assets.token).safeTransfer(party, amount);
    }

    /**
     * @notice Finalize and settle channel with token transfers
     * @param channelId Channel identifier
     */
    function settleChannel(bytes32 channelId) external {
        ChannelCommitment storage commitment = commitments[channelId];
        ChannelAssets storage assets = channelAssets[channelId];

        // Finalize in registry
        channelRegistry.finalizeChannel(channelId, commitment.stateHash);

        // Set challenge deadline (1 day from now)
        assets.challengeDeadline = block.timestamp + CHALLENGE_PERIOD;

        // Mark as pending settlement (not yet settled due to challenge period)
        // After challenge period, withdrawFromChannel can be called

        emit ChannelSettled(channelId, commitment.balance0, commitment.balance1);
    }

    /**
     * @notice Complete settlement after challenge period
     * @param channelId Channel identifier
     * @dev Transfers final balances to parties after challenge period
     */
    function completeSettlement(bytes32 channelId) external {
        ChannelCommitment storage commitment = commitments[channelId];
        ChannelAssets storage assets = channelAssets[channelId];

        require(!assets.settled, "Already settled");
        require(block.timestamp >= assets.challengeDeadline, "Challenge period active");

        // Mark as settled
        assets.settled = true;

        // Transfer final balances
        if (commitment.balance0 > 0) {
            IERC20(assets.token).safeTransfer(assets.party0, commitment.balance0);
        }
        if (commitment.balance1 > 0) {
            IERC20(assets.token).safeTransfer(assets.party1, commitment.balance1);
        }
    }

    /**
     * @notice Update Yellow configuration
     * @param endpoint New Yellow node endpoint
     * @param appId New application ID
     */
    function updateYellowConfig(
        string memory endpoint,
        bytes32 appId
    ) external onlyOwner {
        yellowNodeEndpoint = endpoint;
        yellowAppId = appId;
        emit YellowConfigUpdated(endpoint, appId);
    }

    /**
     * @notice Get channel commitment
     * @param channelId Channel identifier
     * @return Current channel commitment
     */
    function getCommitment(bytes32 channelId) 
        external 
        view 
        returns (ChannelCommitment memory) 
    {
        return commitments[channelId];
    }

    /**
     * @notice Check if channel can be settled
     * @param channelId Channel identifier
     * @return True if ready for settlement
     */
    function canSettle(bytes32 channelId) external view returns (bool) {
        return channelRegistry.canCloseChannel(channelId);
    }

    /**
     * @notice Get Yellow network connection info
     * @return endpoint WebSocket endpoint
     * @return appId Application ID
     */
    function getYellowConfig() 
        external 
        view 
        returns (string memory endpoint, bytes32 appId) 
    {
        return (yellowNodeEndpoint, yellowAppId);
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/access/Ownable2Step.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../../core/DealVault.sol";
import "./YellowChannel.sol";

/**
 * @title YellowRewardDistributor
 * @notice Distributes deal rewards to users via Yellow Network channels
 * @dev Creates individual Yellow channels for each user with their reward allocation
 *
 * Architecture:
 * - DealVault claims rewards from protocol
 * - YellowRewardDistributor creates channels for each user
 * - Each channel allocates (0, userReward) to (vaultAddress, userYellowAddress)
 * - Users can trade their rewards on Yellow Network off-chain
 * - Final settlement returns remaining balance to users
 */
contract YellowRewardDistributor is Ownable2Step {
    using SafeERC20 for IERC20;

    /// @notice DealVault reference
    DealVault public immutable dealVault;

    /// @notice YellowChannel reference
    YellowChannel public immutable yellowChannel;

    /// @notice Mapping from deal ID to user to channel ID
    mapping(uint256 => mapping(address => bytes32)) public userRewardChannels;

    /// @notice Mapping from channel ID to deal ID
    mapping(bytes32 => uint256) public channelDeals;

    /// @notice Mapping to track if rewards were distributed for a deal
    mapping(uint256 => bool) public rewardsDistributed;

    /// @notice Events
    event RewardChannelCreated(
        uint256 indexed dealId,
        address indexed user,
        address indexed yellowAddress,
        bytes32 channelId,
        uint256 rewardAmount
    );

    event RewardsDistributed(
        uint256 indexed dealId,
        uint256 totalRewards,
        uint256 userCount,
        uint256 channelsCreated
    );

    event RewardChannelSettled(
        bytes32 indexed channelId,
        uint256 indexed dealId,
        address indexed user,
        uint256 finalBalance
    );

    /// @notice Custom errors
    error InvalidDeal();
    error RewardsAlreadyDistributed();
    error NoRewardsForUser();
    error ChannelAlreadyExists();
    error InsufficientFunds();

    /**
     * @notice Constructor
     * @param initialOwner Address that will own the contract
     * @param _dealVault DealVault contract address
     * @param _yellowChannel YellowChannel contract address
     */
    constructor(
        address initialOwner,
        address _dealVault,
        address _yellowChannel
    ) Ownable(initialOwner) {
        dealVault = DealVault(_dealVault);
        yellowChannel = YellowChannel(_yellowChannel);
    }

    /**
     * @notice Distribute rewards to users via Yellow channels
     * @param dealId Deal identifier
     * @dev Creates a Yellow channel for each user with their proportional reward
     */
    function distributeRewardsToYellow(uint256 dealId) external onlyOwner {
        // Check rewards haven't been distributed yet
        if (rewardsDistributed[dealId]) revert RewardsAlreadyDistributed();

        // Get deal information
        DealVault.Deal memory deal = dealVault.getDeal(dealId);
        if (deal.dealId == 0) revert InvalidDeal();

        // Get all depositors
        address[] memory depositors = dealVault.getDealDepositors(dealId);
        require(depositors.length > 0, "No depositors");

        uint256 channelsCreated = 0;
        uint256 totalRewardsDistributed = 0;

        // Create channel for each user with their reward allocation
        for (uint256 i = 0; i < depositors.length; i++) {
            address user = depositors[i];
            DealVault.UserDeposit memory userDep = dealVault.getUserDeposit(dealId, user);

            // Skip if no rewards
            if (userDep.rewardShare == 0) continue;

            // Check channel doesn't already exist
            if (userRewardChannels[dealId][user] != bytes32(0)) {
                continue; // Skip if already created
            }

            // Generate unique channel ID
            bytes32 channelId = keccak256(
                abi.encodePacked(
                    "reward",
                    dealId,
                    user,
                    userDep.yellowAddress,
                    block.timestamp,
                    i
                )
            );

            // Open Yellow channel with reward allocation
            // Party0 = DealVault (balance 0)
            // Party1 = User's Yellow address (balance = reward)
            yellowChannel.openYellowChannel(
                channelId,
                address(dealVault),
                userDep.yellowAddress,
                0, // Vault has 0 balance in channel
                userDep.rewardShare, // User gets full reward
                dealId
            );

            // Deposit reward tokens to the channel
            // Note: DealVault must approve this contract first
            IERC20(deal.depositToken).safeTransferFrom(
                address(dealVault),
                address(yellowChannel),
                userDep.rewardShare
            );

            // Record channel mapping
            userRewardChannels[dealId][user] = channelId;
            channelDeals[channelId] = dealId;

            channelsCreated++;
            totalRewardsDistributed += userDep.rewardShare;

            emit RewardChannelCreated(
                dealId,
                user,
                userDep.yellowAddress,
                channelId,
                userDep.rewardShare
            );
        }

        // Mark as distributed
        rewardsDistributed[dealId] = true;

        emit RewardsDistributed(dealId, totalRewardsDistributed, depositors.length, channelsCreated);
    }

    /**
     * @notice Create individual reward channel for a specific user
     * @param dealId Deal identifier
     * @param user User address
     * @return channelId Created channel ID
     */
    function createRewardChannelForUser(uint256 dealId, address user)
        external
        onlyOwner
        returns (bytes32 channelId)
    {
        // Get deal and user deposit info
        DealVault.Deal memory deal = dealVault.getDeal(dealId);
        if (deal.dealId == 0) revert InvalidDeal();

        DealVault.UserDeposit memory userDep = dealVault.getUserDeposit(dealId, user);
        if (userDep.rewardShare == 0) revert NoRewardsForUser();

        // Check channel doesn't exist
        if (userRewardChannels[dealId][user] != bytes32(0)) {
            revert ChannelAlreadyExists();
        }

        // Generate channel ID
        channelId = keccak256(
            abi.encodePacked(
                "reward",
                dealId,
                user,
                userDep.yellowAddress,
                block.timestamp
            )
        );

        // Open Yellow channel
        yellowChannel.openYellowChannel(
            channelId,
            address(dealVault),
            userDep.yellowAddress,
            0,
            userDep.rewardShare,
            dealId
        );

        // Transfer reward tokens to channel
        IERC20(deal.depositToken).safeTransferFrom(
            address(dealVault),
            address(yellowChannel),
            userDep.rewardShare
        );

        // Record mapping
        userRewardChannels[dealId][user] = channelId;
        channelDeals[channelId] = dealId;

        emit RewardChannelCreated(
            dealId,
            user,
            userDep.yellowAddress,
            channelId,
            userDep.rewardShare
        );
    }

    /**
     * @notice Settle reward channel and return remaining funds to user
     * @param dealId Deal identifier
     * @param user User address
     */
    function settleRewardChannel(uint256 dealId, address user) external {
        bytes32 channelId = userRewardChannels[dealId][user];
        require(channelId != bytes32(0), "Channel not found");

        // Settle the Yellow channel
        yellowChannel.settleChannel(channelId);

        // Get final balance
        YellowChannel.ChannelCommitment memory commitment = yellowChannel.getCommitment(channelId);

        emit RewardChannelSettled(channelId, dealId, user, commitment.balance1);
    }

    /**
     * @notice Get user's reward channel ID
     * @param dealId Deal identifier
     * @param user User address
     * @return channelId Channel ID (bytes32(0) if not created)
     */
    function getUserRewardChannel(uint256 dealId, address user)
        external
        view
        returns (bytes32 channelId)
    {
        return userRewardChannels[dealId][user];
    }

    /**
     * @notice Get deal ID for a reward channel
     * @param channelId Channel identifier
     * @return dealId Deal ID
     */
    function getChannelDeal(bytes32 channelId)
        external
        view
        returns (uint256 dealId)
    {
        return channelDeals[channelId];
    }

    /**
     * @notice Check if rewards have been distributed for a deal
     * @param dealId Deal identifier
     * @return True if distributed
     */
    function areRewardsDistributed(uint256 dealId)
        external
        view
        returns (bool)
    {
        return rewardsDistributed[dealId];
    }

    /**
     * @notice Get reward channel information for a user
     * @param dealId Deal identifier
     * @param user User address
     * @return channelId Channel ID
     * @return commitment Channel commitment data
     */
    function getUserRewardChannelInfo(uint256 dealId, address user)
        external
        view
        returns (
            bytes32 channelId,
            YellowChannel.ChannelCommitment memory commitment
        )
    {
        channelId = userRewardChannels[dealId][user];
        if (channelId != bytes32(0)) {
            commitment = yellowChannel.getCommitment(channelId);
        }
    }
}

